import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import "./LoginPage.scss";

import TextInput from "../../basic-components/text-input/TextInput";

import { userActions } from "../../actions/user-actions";

class LoginPage extends React.Component {
 constructor(props) {
        super(props);

        // reset login status
        this.props.dispatch(userActions.logout());

        this.state = {
            username: '',
            password: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        const { dispatch } = this.props;
        if (username && password) {
            dispatch(userActions.login(username, password));
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { username, password, submitted } = this.state;
        return (
            <div className="login-page">
                <div className="login-page__login-form">
                    <h2>Login</h2>
                    <form name="form" onSubmit={this.handleSubmit}>
                        <div className={'form-group form-group--border' + (submitted && !username ? ' has-error' : '')}>
                            <TextInput placeholder="Username" name="username" className="form-control" type="text" value={username} onChange={this.handleChange} />
                            {submitted && !username &&
                                <div className="help-block">Username is required</div>
                            }
                        </div>
                        <div className={'form-group form-group--border' + (submitted && !password ? ' has-error' : '')}>
                        <TextInput placeholder="Password" name="password" className="form-control" type="password" value={password} onChange={this.handleChange} />
                            {submitted && !password &&
                                <div className="help-block">Password is required</div>
                            }
                        </div>
                        <div className="form-group">
                            <button className="form-group__button">Login</button>
                            {loggingIn &&
                                <img src="data:image/gif;base64," alt="loading" />
                            }
                            <Link to="/register" className="btn btn-link">Register</Link>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    const { loggingIn } = state.authentication;
    return {
        loggingIn
    };
}

export default connect(mapStateToProps)(LoginPage);
